import * as actions from './actions'

const initialState = {
    activeLink: 'home',
}

const reducer = (state = initialState, action) => {
    switch (action) {
        case actions.ACTIVE_PAGE:
            return {
                activePage: action.active
            };
        default:
            return state;
    }
}

export default reducer
