import Colors from '../constants/colors';
import styled from 'styled-components';


export default class index extends React.Component {

    static async getInitialProps() {
        return {}
    }

    render() {
        return (
            <div className="mainContainer">
                <NavBar>
                    <Logo>HOMINIAN</Logo>

                    <PageLinks>
                        <Links>Products</Links>
                        <Links>Blog</Links>
                    </PageLinks>
                </NavBar>

                <MainContent>
                    <Quote>
                        <p>
                            â€œLook again at that dot. That's here. That's home. That's us. On it everyone you love, everyone you know, everyone you ever heard of, every human being who ever was, lived out their lives. The aggregate of our joy and suffering, thousands of confident religions, ideologies, and economic doctrines, every hunter and forager, every hero and coward, every creator and destroyer of civilization, every king and peasant, every young couple in love, every mother and father, hopeful child, inventor and explorer, every teacher of morals, every corrupt politician, every "superstar," every "supreme leader," every saint and sinner in the history of our species lived there-on a mote of dust suspended in a sunbeam.
                        </p>
                    </Quote>
                </MainContent>

                <Footer>
                    <LogoFooter>HOMINIAN</LogoFooter>
                </Footer>

                <style jsx>
                    {`
                        .mainContainer{
                            width: 100%;
                            background: white;
                            font-family: 'Circular-Book', Arial, sans-serif;
                        }                    
                     
                  `}
                </style>

                <style global jsx>
                    {`
                        body {
                            margin: 0;
                            padding: 0;
                            border: 0;
                        }
                       
                    `}
                </style>

            </div>
        )

    }



}


const NavBar = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    padding-top: 32px;
    padding-bottom: 32px;
`
const Logo = styled.div`
    font-size: 32px;
    padding-left: 42px;
    color: grey;
    font-family: sans-serif;

    &:hover{
        cursor: pointer;
    }
`
const PageLinks = styled.div`
    display: flex;
    flex-direction: row;
    margin-right: 32px;
    padding-top: 8px;
    padding-bottom: 8px;
`
const Links = styled.a`
    font-size: 24px;
    color: grey;
    padding-top: 4px;
    padding-bottom: 4px;
    padding-right: 16px;
    padding-left: 16px;
    border: 1px solid transparent;
    border-radius: 10px;
    transition: border 0.5s;
    transition: color 0.5s;
    
    

    &:hover{
        cursor: pointer;
        border: 1px solid grey;
        color: ${Colors.transparentBlack};
    }

`

const MainContent = styled.div`
    width: 100%;
    height: 164vh;
    background: white;
`

const Quote = styled.div`
    margin: 128px;
`

const Footer = styled.div`
    display: flex;
    width: 100%;
    height: 128px;
    background-color: ${Colors.footerGrey};
    align-items: center;
    justify-content: flex-end
`

const LogoFooter = styled.div`
    font-size: 24px;
    padding-right: 42px;
    color: grey;
    font-family: sans-serif;

    &:hover{
        cursor: pointer;
    }
`
