import Lottie from 'react-lottie';
import * as earthAnimation from '../../assets/animations/globe.json'
import Link from 'next/link'

import Colors from '../../constants/colors';
import styled from 'styled-components';


export default class Layout extends React.Component {


    render() {

        return (
            <div className="mainContainer">
                <NavBar>
                    <Link href>
                        <Logo>HOMINIAN</Logo>
                    </Link>
                    <PageLinks>
                        <Link href="/products">
                            <Links>Products</Links>
                        </Link>
                        <Link href="/blog">
                            <Links>Blog</Links>
                        </Link>
                    </PageLinks>
                </NavBar>

                {this.props.children}

                <Footer>
                    <LogoFooter>HOMINIAN</LogoFooter>
                </Footer>

                <style jsx>
                    {`
                        .mainContainer{
                            width: 100%;
                            background: white;
                            font-family: 'Circular-Book', Arial, sans-serif;
                        }                    
                     
                  `}
                </style>

                <style global jsx>
                    {`
                        body {
                            margin: 0;
                            padding: 0;
                            border: 0;
                        }
                       
                    `}
                </style>

            </div>
        )

    }



}


const AnimationEarth = styled.div`
height: 300px;
width: 300px;
padding: 4px;

`



const NavBar = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    padding-top: 32px;
    padding-bottom: 32px;
`
const Logo = styled.div`
    font-size: 32px;
    padding-left: 42px;
    color: grey;
    font-family: sans-serif;

    &:hover{
        cursor: pointer;
    }
`
const PageLinks = styled.div`
    display: flex;
    flex-direction: row;
    margin-right: 32px;
    padding-top: 8px;
    padding-bottom: 8px;
`
const Links = styled.a`
    font-size: 24px;
    color: grey;
    padding-top: 4px;
    padding-bottom: 4px;
    padding-right: 16px;
    padding-left: 16px;
    border: 1px solid transparent;
    border-radius: 10px;
    transition: border 0.5s;
    transition: color 0.5s;
    
    

    &:hover{
        cursor: pointer;
        border: 1px solid grey;
        color: ${Colors.transparentBlack};
    }

`

const Footer = styled.div`
    display: flex;
    width: 100%;
    height: 128px;
    background-color: ${Colors.footerGrey};
    align-items: center;
    justify-content: flex-end
`

const LogoFooter = styled.div`
    font-size: 24px;
    padding-right: 42px;
    color: grey;
    font-family: sans-serif;

`
